import { useState } from "react";

const DiaryEditor = () => {
  const [state, setState] = useState({
    author: "",
    content: "",
    emotion: ""
  });
  // const [author, setAuthor] = useState("");
  // const [content, setContent] = useState("");
  const handleChangeState = (e) => {
    console.log(e.target.name);
    console.log(e.target.value);
    setState({
      ...state,
      [e.target.name] : e.target.value,
    });
  };
  return <div className="DiaryEditor">
    <h2>오늘의 일기</h2>
    <div>
      <input 
      name="author"
      value={state.author} 
      onChange={handleChangeState}/>
    </div>
    <div>
      <textarea 
      name="content"
      value={state.content}
      onChange={handleChangeState}
      />
    </div>
    <div>
      <select value={1} name="emotion" value={state.emotion} onChange={handleChangeState}>1</select>
      <select value={2} name="emotion" value={state.emotion} onChange={handleChangeState}>2</select>
      <select value={3} name="emotion" value={state.emotion} onChange={handleChangeState}>3</select>
      <select value={4} name="emotion" value={state.emotion} onChange={handleChangeState}>4</select>
      <select value={5} name="emotion" value={state.emotion} onChange={handleChangeState}>5</select>
    </div>
  </div>;
}
export default DiaryEditor;
